import { useParams } from "react-router";
import Mvideo from "./Mvideo";
import { useState } from "react";
const MMvideo = () => {
  let { id } = useParams();
  const [data, setdata] = useState(0);
  async function all() {
    console.log(id);
    const res = await fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`);
    console.log(res);
    const data2 = await res.json();
    setTimeout(() => {
      setdata(data2.url);
    }, 2000);
  }
  all();
  return <Mvideo link={data} />;
};

export default MMvideo;
