import "./movies.css";
import { useRef } from "react";
import Smovies from "./smovie";

const Movies = () => {
  const hscroll = useRef(null);
  const scroll = (sender) => {
    console.log(sender.current.parentNode);
    var obj = sender.parentNode;
    var cnode = obj.childNodes;
    var width = obj.scrollWidth;
    var currscroll = obj.scrollLeft;
    var ss = currscroll - 400;
    console.log(ss);
    console.log(width);
    if (ss < 0) {
      console.log("hello");
      ss = 0;
      cnode[3].style.display = "none";
    }
    console.log(currscroll);
    console.log(ss);
    obj.scrollLeft = ss;
    cnode[5].style.display = "flex";
  };
  function scroll3(sender) {}
  return (
    <>
      <div className="container2">
        <div className="main-2" ref={hscroll}>
          <div
            className="mover-left"
            onClick={() => {
              scroll(hscroll.current);
            }}
          >
            {"<"}
          </div>
          <Smovies />
          <Smovies />
          <Smovies />
