          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <div className="mover-right">{">"}</div>
        </div>
      </div>
    </>
  );
};

export default Movies;
