import Skeleton, { SkeletonTheme } from "react-loading-skeleton";
import "./movies.css";
import { useState, useEffect } from "react";
const Smovies = (props) => {
  const data = null;
  setTimeout(() => {
    data = 1;
  }, 2000);

  console.log(props);
  return (
    <>
      {data && (
        <div className="card">
          <div className="img">
            <img loading="lazy" src={props.imgUrl} alt="" />
          </div>
          <h2 className="title">{props.title}</h2>
        </div>
      )}
      {!data && (
        <div className="card">
          <SkeletonTheme color="#202020" highlightColor="#2b2b2a">
            <Skeleton height={256} />
            <Skeleton />
          </SkeletonTheme>
        </div>
      )}
    </>
  );
};

export default Smovies;
