import Skeleton, { SkeletonTheme } from "react-loading-skeleton";
import { useRef } from "react";
import "./movies.css";
import { useState, useEffect } from "react";

const Smovies = (props) => {
  const card = useRef(null);
  const [data, setData] = useState(null);
  const [hover, setHover] = useState("precontent");
  useEffect(() => {
    setTimeout(() => {
      setData(1);
    }, 2000);
  });
  // card.current.addEventListner("mouseover", (e) => {
  //   card.current.classList.add("shiftLeft");
  //   card.current.classList.add("shiftRight");
  //   card.current.classList.add("darker");
  // });
  // console.log(card);
  // tiles.addEventListener("mouseover", (e) => {
  //   tiles.classList.add("shiftLeft");
  //   // if target has next siblings, move them to the right
  //   tiles.classList.add("shiftRight");
  //   // make other tiles darker
  //   tiles.classList.add("darker");
  // });

  // tiles.addEventListener("mouseout", (e) => {
  //   // move all tiles back to initial position

  //   tiles.classList.remove("shiftLeft");
  //   tiles.classList.remove("shiftRight");
  //   tiles.classList.remove("darker");
  // });
  function move() {
    setHover("null");
  }

  function move2() {
    setHover("precontent");
  }

  return (
    <>
      {data && (
        <div className={`card`} ref={card}>
          <div className="img" onMouseOver={move} onMouseLeave={move2}>
            <img loading="lazy" src={props.imgUrl} alt="" />
          </div>
          <div className={`content ${hover}`} onMouseLeave={move}>
            <div className="firstc">
              <button className="play">
                <i className="fas fa-play"></i>
              </button>
              <h2>{props.title}</h2>
              <div className="rating">{props.rating}</div>
            </div>
            <div className="secondc">
              <div className="overview">
                overview
                <p>{props.over}</p>
              </div>
            </div>
          </div>
        </div>
      )}
      {!data && (
        <div className="card">
          <SkeletonTheme color="#202020" highlightColor="#2b2b2a">
            <Skeleton height={256} />
            <Skeleton />
          </SkeletonTheme>
        </div>
      )}
    </>
  );
};

export default Smovies;
