import { useParams, useHistory } from "react-router";
import Loader from "react-loader-spinner";
import { useState } from "react";
import { useRef } from "react";
import Plyr from "plyr";
import "./movies.css";
import { useEffect } from "react";
const Video = () => {
  const history = useHistory();
  let { id } = useParams();
  const [link, setlink] = useState([]);

  const disable = useRef();

  const icon = useRef();

  useEffect(() => {
    fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`)
      .then((res) => {
        return res.json();
      })
      .then((data2) => {
        const player = new Plyr("#player");
        console.log(player);
        setlink(data2.url);

        setTimeout(() => {
          disable.current.style.display = "none";
          icon.current.style.display = "block";
        }, 3000);
      });
  });
  return (
    <>
      <div className="loaderarea" ref={disable}>
        <Loader
          type="Puff"
          color="#00BFFF"
          height={100}
          width={100}
          timeout={3000} //3 secs
        />
      </div>
      <>
        <div
          ref={icon}
          className="back-btn"
          onClick={() => {
            history.goBack();
          }}
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            x="0px"
            y="0px"
            width="50"
            height="50"
            viewBox="0 0 226 226"
            style=" fill:#000000;"
          >
            <g
              fill="none"
              fill-rule="nonzero"
              stroke="none"
              stroke-width="1"
              stroke-linecap="butt"
              stroke-linejoin="miter"
              stroke-miterlimit="10"
              stroke-dasharray=""
              stroke-dashoffset="0"
              font-family="none"
              font-weight="none"
              font-size="none"
              text-anchor="none"
              style="mix-blend-mode: normal"
            >
              <path d="M0,226v-226h226v226z" fill="#ffffff"></path>
              <g fill="#000000">
                <path d="M54.04578,63.22703c-1.16531,0.07062 -2.26,0.58266 -3.05453,1.44781l-46.40062,48.32516l46.40062,48.32516c1.11234,1.18297 2.77203,1.67734 4.34344,1.28891c1.57141,-0.38844 2.825,-1.58906 3.26641,-3.14281c0.45906,-1.57141 0.03531,-3.24875 -1.11234,-4.39641l-36.03641,-37.55484h195.50766c1.62437,0.01766 3.14281,-0.82984 3.97266,-2.24234c0.81219,-1.4125 0.81219,-3.14281 0,-4.55531c-0.82984,-1.4125 -2.34828,-2.26 -3.97266,-2.24234h-195.50766l36.03641,-37.55484c1.32422,-1.32422 1.695,-3.31938 0.91812,-5.01438c-0.75922,-1.71266 -2.48953,-2.77203 -4.36109,-2.68375z"></path>
              </g>
            </g>
          </svg>
        </div>
        <div className="vcontainer">
          <video
            id="player"
            src={link}
            controls
            data-poster="/path/to/poster.jpg"
          ></video>
        </div>
      </>
    </>
  );
};

export default Video;
