import Skeleton, { SkeletonTheme } from "react-loading-skeleton";
import { useRef } from "react";
import "./movies.css";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
const Smovies = (props) => {
  const card = useRef(null);
  const [data, setData] = useState(false);

  useEffect(() => {
    setTimeout(() => {
      setData(1);
    }, 1000);
  }, []);
  const [hover, setHover] = useState("precontent");
  function move() {
    setHover("null");
  }
  const width = window.innerWidth;
  function calli() {
    if (width < 500) {
      fetch(
        `https://netflix-ankit.ankitzxi05.repl.co/${encodeURIComponent(
          props.link
        )}`
      )
        .then((res) => {
          return res.json();
        })
        .then((data2) => {
          const enurl = window.btoa(data2.url);
          const murl = "https://netflix-techarmy.me./index2.html?name=" + enurl;
          window.location.assign(murl);
        });
    } else {
      const murl = "/video/" + encodeURIComponent(props.link);
      window.location.assign(murl);
    }
  }
  // function move2() {
  //   setHover("precontent");
  // }

  return (
    <>
      {data && (
        <div className="card" ref={card}>
          <div className="img">
            <img loading="lazy" src={props.imgUrl} alt="" onMouseOver={move} />
          </div>
          <div className={`content ${hover}`} onMouseOver={move}>
            <div className="firstc">
              <div className="overview">
                <p className="mtitle2">{props.title}</p>
                <p>{props.over}</p>
              </div>
            </div>
            <div className="secondc">
              <div className="playarea">
                {/* <Link to={`/video/${encodeURIComponent(props.link)}`}> */}
                <button className="play" onClick={calli}>
                  <i className="fas fa-play"></i>
                </button>
                {/* </Link> */}
                Watch Now
              </div>
              <div className="rating">{props.rating}</div>
            </div>
          </div>
        </div>
      )}
      {!data && (
        <div className="kk">
          <SkeletonTheme color="#202020" highlightColor="#2b2b2a">
            <Skeleton height={256} />
            <Skeleton />
          </SkeletonTheme>
        </div>
      )}
    </>
  );
};

export default Smovies;
