import Video from "./video";
import { useParams } from "react-router";
import { useState } from "react";
import { useEffect } from "react";

// Lazy load the YouTube player

const Mvideo = () => {
  let { id } = useParams();
  const [ele, setele] = useState([]);
  const width = window.innerWidth;
  useEffect(() => {
    if (width > 500) {
      let arr = [];
      arr.push(<Video key={width} />);
      setele(arr);
    } else {
      alert("dhhdh");
      window.location.assign(`https://netflix-ankit.ankitzxi05.repl.co/${id}`);
      // fetch(`https://netflix-ankit.ankitzxi05.repl.co/${nid}`)
      //   .then((res) => {
      //     alert("dhhdh2");
      //     return res.json();
      //   })
      //   .then((data2) => {
      //     alert(data2.url);
      //     const enurl = window.btoa(data2.url);
      //     const murl = "https://netflix-techarmy.me./index2.html?name=" + enurl;
      //     alert("reacehr");
      //     window.location.assign(murl);
      //   });
    }
  }, [id]);

  return <>{ele}</>;
};

export default Mvideo;
