import { useParams, useHistory } from "react-router";
import Loader from "react-loader-spinner";
import { useState, useEffect } from "react";
import { useRef } from "react";
import Plyr from "plyr";
import "./movies.css";
const Video = () => {
  const history = useHistory();
  const player = new Plyr("#player");
  let { id } = useParams();
  const [link, setlink] = useState([]);
  const disable = useRef();
  const icon = useRef();
  useEffect(() => {
    setTimeout(() => {
      disable.current.style.display = "none";
      icon.current.style.display = "block";
    }, 3000);
    fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data.url);
        setlink(data.url);
      });
  }, [id]);
  console.log(player);
  return (
    <>
      <div className="loaderarea" ref={disable}>
        <Loader
          type="Puff"
          color="#00BFFF"
          height={100}
          width={100}
          timeout={3000} //3 secs
        />
      </div>
      <div
        className="back-btn"
        onClick={() => {
          history.goBack();
        }}
      >
        <i class="fas fa-long-arrow-alt-left" ref={icon}></i>
      </div>
      <div className="vcontainer">
        <video
          id="player"
          playsinline
          controls
          data-poster="/path/to/poster.jpg"
        >
          <source src={link} type="video/mp4" />
        </video>
      </div>
    </>
  );
};

export default Video;
