import { useParams, useHistory } from "react-router";
import Loader from "react-loader-spinner";

import { useRef } from "react";
import Plyr from "plyr";
import "./movies.css";
const Video = () => {
  const history = useHistory();
  const player = new Plyr("#player");
  let { id } = useParams();
  // const [link, setlink] = useState([]);
  const disable = useRef();
  const videol = useRef();
  const icon = useRef();

  fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`)
    .then((res) => {
      return res.json();
    })
    .then((data) => {
      videol.current.src = data.url;
      setTimeout(() => {
        disable.current.style.display = "none";
        icon.current.style.display = "block";
      }, 3000);
    });
  console.log(player);
  return (
    <>
      <div className="loaderarea" ref={disable}>
        <Loader
          type="Puff"
          color="#00BFFF"
          height={100}
          width={100}
          timeout={3000} //3 secs
        />
      </div>
      <div
        ref={icon}
        className="back-btn"
        onClick={() => {
          history.goBack();
        }}
      >
        <i className="fas fa-long-arrow-alt-left"></i>
      </div>
      <div className="vcontainer">
        <video
          id="player"
          playsinline
          controls
          data-poster="/path/to/poster.jpg"
        >
          <source src="" type="video/mp4" ref={videol} />
        </video>
      </div>
    </>
  );
};

export default Video;
