import "./movies.css";

import Smovies from "./smovie";
function scroll3(sender) {
  var obj = sender.parentNode;
  var cnode = obj.childNodes;
  var width = obj.scrollWidth;
  var currscroll = obj.scrollLeft;
  var ss = currscroll - 400;
  console.log(ss);
  console.log(width);
  if (ss < 0) {
    console.log("hello");
    ss = 0;
    cnode[3].style.display = "none";
  }
  console.log(currscroll);
  console.log(ss);
  obj.scrollLeft = ss;
  cnode[5].style.display = "flex";
}
const Movies = () => {
  return (
    <>
      <div className="container2">
        <div className="main-2">
          <div onClick={scroll3(this)} className="mover-left">
            {"<"}
          </div>
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <div className="mover-right">{">"}</div>
        </div>
      </div>
    </>
  );
};

export default Movies;
