import React, { Component } from "react";
class Counter extends Component {
  state = {
    count: 0,
  };
  render() {
    console.log(this.props);
    return (
      <div>
        <span>{this.number()}</span>
        <button onClick={this.inc} className="btn btn-primary ">
          Increment
        </button>
        <button onClick={this.dec} className="btn btn-danger ">
          Decrement
        </button>
      </div>
    );
  }
  format() {
    const { count } = this.state;
    return count === 0 ? "zero" : count;
  }
  inc = () => {
    console.log("Ankit");
    this.setState({ count: this.state.count + 1 });
  };
  dec = () => {
    this.setState({ count: this.state.count - 1 });
  };
  number = () => {
    console.log("Ankitmdjd");
    if (this.state.count == 0) {
      return <button className="btn  btn-secondary btn-sm">Zero</button>;
    }
    return (
      <button className="btn btn-secondary  btn-sm ">{this.state.count}</button>
    );
  };
}

export default Counter;
