import { useParams, useHistory } from "react-router";
import Loader from "react-loader-spinner";
import { useState } from "react";
import { useRef } from "react";
import Plyr from "plyr";
import "./movies.css";
import { useEffect } from "react";
const Video = () => {
  const history = useHistory();
  let { id } = useParams();
  const [link, setlink] = useState([]);
  const height = window.innerWidth;
  console.log(height);
  const disable = useRef();
  const [hh, sethh] = useState(false);
  if (height < 500) {
    sethh(1);
  }
  const icon = useRef();
  setTimeout(() => {
    if (height > 500) {
      disable.current.style.display = "none";
    }
  }, 3000);
  useEffect(() => {
    fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`)
      .then((res) => {
        return res.json();
      })
      .then((data2) => {
        if (height > 500) {
          setlink(data2.url);
          alert(data2.url);
          const player = new Plyr("#player");
          console.log(player);
          setTimeout(() => {
            icon.current.style.display = "block";
          }, 3000);
        }
      });
  });

  return (
    <>
      {!hh && (
        <>
          <div className="loaderarea" ref={disable}>
            <Loader
              type="Puff"
              color="#00BFFF"
              height={100}
              width={100}
              timeout={3000} //3 secs
            />
          </div>

          <div
            ref={icon}
            className="back-btn"
            onClick={() => {
              history.goBack();
            }}
          >
            <i className="fas fa-long-arrow-alt-left"></i>
          </div>
          <div className="vcontainer">
            <video
              id="player"
              src={link}
              controls
              data-poster="/path/to/poster.jpg"
            ></video>
          </div>
        </>
      )}
      {hh && <h1>Open in Mx player</h1>}
    </>
  );
};

export default Video;
