import "./movies.css";
import { useRef } from "react";
import { useState } from "react";
import Smovies from "./smovie";
import { useEffect } from "react";

const Movies = (props) => {
  const API_KEY = "api_key=0b640427f9aaaa52b5a46e8def202965";
  const BASE_URL = "https://api.themoviedb.org/3";
  const IMG_URL = "https://image.tmdb.org/t/p/w500";
  const searchURL = BASE_URL + "/search/movie?" + API_KEY;
  let mainlink = [];
  let mainurl = [];
  let maintitle = [];
  let mainarr = [];
  let mindex = 0;
  const [value, setvalue] = useState(0);
  useEffect(() => {
    setvalue(1);
  }, []);
  const [compo, setCompo] = useState([]);
  useEffect(() => {
    dataCall();
  }, [value]);

  async function search(url) {
    const req2 = await fetch(url);
    const data2 = await req2.json();
    return data2;
  }
  async function jsoncall(url) {
    const req2 = await fetch(url);
    const data2 = await req2.json();
    return data2;
  }
  async function loop(j, k, datam) {
    for (let i = j; i < k; i++) {
      const details = await search(
        searchURL + "&query=" + datam[i].name.replace(/ *\([^)]*\) */g, "")
      );
      if (
        details.results.length !== 0 &&
        details.results[0].poster_path !== null
      ) {
        mainlink[mindex] = await datam[i].dlink;
        mainurl[mindex] = IMG_URL + details.results[0].poster_path;
        maintitle[mindex] = await details.results[0].title;
        mindex++;
      }
    }
    mainarr.push(mainlink, mainurl, maintitle);
    return mainarr;
  }
  //data fetch
  async function dataCall() {
    const datam = await jsoncall("./config.json");
    const createarr = await loop(props.j, props.k, datam);
    const compo = await setting(createarr);
    setCompo(compo);
  }

  async function setting(arr) {
    const length = await arr[0].length;
    const compoarr = [];
    for (let i = 0; i < length; i++) {
      compoarr.push(<Smovies key={i} imgUrl={arr[1][i]} title={arr[2][i]} />);
    }
    return compoarr;
  }

  //left and right scrolls
  const hscroll = useRef(null);
  const scrollL = (sender) => {
    var obj = sender.parentNode;
    var currscroll = obj.scrollLeft;
    var ss = currscroll - 400;
    if (ss < 0) {
      ss = 0;
    }
    obj.scrollLeft = ss;
  };
  function scrollR(sender) {
    var obj = sender.parentNode;
    var width = obj.scrollWidth;
    var currscroll = obj.scrollLeft;
    var ss = currscroll + 400;
    if (ss >= width) {
      ss = width;
    }
    obj.scrollLeft = ss;
  }

  return (
    <>
      <div className="container2">
        <div className="main-2" ref={hscroll}>
          <div
            className="mover-left"
            onClick={() => {
              scrollL(hscroll.current);
            }}
          >
            {"<"}
          </div>
          {compo}
          <div
            className="mover-right"
            onClick={() => {
              scrollR(hscroll.current);
            }}
          >
            {">"}
          </div>
        </div>
      </div>
    </>
  );
};

export default Movies;
