import { useParams } from "react-router";
import Mvideo from "./Mvideo";
import { useState } from "react";
import { useEffect } from "react";
const MMvideo = () => {
  let { id } = useParams();
  const [data, setdata] = useState([]);
  useEffect(() => {
    fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`)
      .then((res) => {
        return res.json();
      })
      .then((data2) => {
        let arr = [];
        arr.push(<Mvideo key={1} link={data2.url} />);
        setdata(arr);
      });
  }, [id]);
  return <>{data}</>;
};

export default MMvideo;
