import Video from "./video";
import { useParams } from "react-router";

// Lazy load the YouTube player

const Mvideo = () => {
  let { id } = useParams();
  const width = window.innerWidth;

  function call() {
    if (width > 500) {
      return <Video />;
    } else {
      fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`)
        .then((res) => {
          return res.json();
        })
        .then((data2) => {
          const enurl = window.btoa(data2.url);
          const murl = "https://netflix-techarmy.me./index2.html?name=" + enurl;
          window.location.assign(murl);
        });
    }
  }

  return <>{call()}</>;
};

export default Mvideo;
