import Video from "./video";
import { useParams } from "react-router";
import { useState } from "react";
const Mvideo = () => {
  let { id } = useParams();
  const width = window.innerWidth;
  const [data, setdata] = useState(0);
  function call() {
    if (width > 500) {
      return <Video />;
    } else {
      fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`)
        .then((res) => {
          return res.json();
        })
        .then((data2) => {
          setdata(data2.url);
        });
    }
  }
  return (
    <>
      {call()}
      <video src={data} controls data-poster="/path/to/poster.jpg" />
    </>
  );
};

export default Mvideo;
