import "./movies.css";
import { useRef } from "react";
import { useState, UseEffect } from "react";
import Smovies from "./smovie";

const Movies = () => {
  const [opacity, setOpacity] = useState(null);
  const hscroll = useRef(null);
  const Rscroll = useRef(null);
  const Lscroll = useRef(null);

  const scrollL = (sender) => {
    Rscroll.current.style.opacity = "1";
    var obj = sender.parentNode;
    var cnode = obj.childNodes;
    var width = obj.scrollWidth;
    var currscroll = obj.scrollLeft;
    var ss = currscroll - 400;
    if (ss < 0) {
      ss = 0;
      Lscroll.current.style.opacity = "0.4";
    }
    obj.scrollLeft = ss;
  };

  function scrollR(sender) {
    Lscroll.current.style.opacity = "1";
    var obj = sender.parentNode;
    var cnode = obj.childNodes;
    var width = obj.scrollWidth;
    var currscroll = obj.scrollLeft;
    var ss = currscroll + 400;
    if (ss >= width) {
      Rscroll.current.style.opacity = "0.4";
      ss = width;
    }
    obj.scrollLeft = ss;
  }

  return (
    <>
      <div className="container2">
        <div className="main-2" ref={hscroll}>
          <div
            className="mover-left"
            onClick={() => {
              scrollL(hscroll.current);
            }}
            ref={Lscroll}
          >
            {"<"}
          </div>
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <div
            className="mover-right"
            onClick={() => {
              scrollR(hscroll.current);
            }}
            useRef={Rscroll}
          >
            {">"}{" "}
          </div>
        </div>
      </div>
    </>
  );
};

export default Movies;
