import "./movies.css";
import { useRef } from "react";
import Smovies from "./smovie";

const Movies = () => {
  const hscroll = useRef(null);
  const scrollL = (sender) => {
    console.log(sender);
    var obj = sender.parentNode;
    var cnode = obj.childNodes;
    var width = obj.scrollWidth;
    var currscroll = obj.scrollLeft;
    var ss = currscroll - 400;
    console.log(ss);
    console.log(width);
    if (ss < 0) {
      console.log("hello");
      ss = 0;
    }
    console.log(currscroll);
    console.log(ss);
    obj.scrollLeft = ss;
  };

  function scrollR(sender) {
    var obj = sender.parentNode;
    var cnode = obj.childNodes;
    cnode[3].style.display = "flex";

    var width = obj.scrollWidth;
    var currscroll = obj.scrollLeft;
    console.log(width);
    var ss = currscroll + 400;
    console.log("ss", ss);
    if (ss >= width) {
      console.log("hello");
      cnode[5].style.display = "none";
      ss = width;
    }
    obj.scrollLeft = ss;
  }

  return (
    <>
      <div className="container2">
        <div className="main-2" ref={hscroll}>
          <div
            className="mover-left"
            onClick={() => {
              scrollL(hscroll.current);
            }}
          >
            {"<"}
          </div>
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <Smovies />
          <div
            className="mover-right"
            onClick={() => {
              scollR(hscroll.current);
            }}
          >
            {">"}{" "}
          </div>
        </div>
      </div>
    </>
  );
};

export default Movies;
