import Video from "./video";
import { useParams } from "react-router";
const Mvideo = () => {
  let { id } = useParams();
  const width = window.innerWidth;
  function call() {
    if (width > 500) {
      return <Video />;
    } else {
      fetch(`https://netflix-ankit.ankitzxi05.repl.co/${id}`)
        .then((res) => {
          return res.json();
        })
        .then((data2) => {
          const ele = (
            <video src={data2.url} controls data-poster="/path/to/poster.jpg" />
          );
          return ele;
        });
    }
  }
  return <>{call()}</>;
};

export default Mvideo;
